{
  "rest": {
    "baseUrl": "/api/v1",
    "endpoints": [
      {
        "path": "/sessions",
        "methods": [
          "GET",
          "POST",
          "DELETE"
        ],
        "description": "Session management"
      },
      {
        "path": "/sessions/:id",
        "methods": [
          "GET",
          "DELETE"
        ],
        "description": "Individual session operations"
      },
      {
        "path": "/contexts",
        "methods": [
          "GET",
          "POST",
          "PUT",
          "DELETE"
        ],
        "description": "Context management"
      },
      {
        "path": "/contexts/:id",
        "methods": [
          "GET",
          "PUT",
          "DELETE"
        ],
        "description": "Individual context operations"
      },
      {
        "path": "/contexts/:id/execute",
        "methods": [
          "POST"
        ],
        "description": "Execute commands in context"
      },
      {
        "path": "/api-keys",
        "methods": [
          "GET",
          "POST"
        ],
        "description": "API key management"
      },
      {
        "path": "/api-keys/:id",
        "methods": [
          "DELETE"
        ],
        "description": "Individual API key operations"
      },
      {
        "path": "/health",
        "methods": [
          "GET"
        ],
        "description": "Health check"
      }
    ],
    "authentication": {
      "methods": [
        "jwt",
        "apikey",
        "session"
      ],
      "headers": {
        "jwt": "Authorization: Bearer <token>",
        "apikey": "X-API-Key: <key>"
      }
    }
  },
  "grpc": {
    "services": [
      {
        "name": "SessionService",
        "methods": [
          "CreateSession",
          "GetSession",
          "DeleteSession",
          "ListSessions"
        ]
      },
      {
        "name": "ContextService",
        "methods": [
          "CreateContext",
          "GetContext",
          "ExecuteCommand"
        ]
      },
      {
        "name": "HealthService",
        "methods": [
          "Check",
          "Watch"
        ]
      }
    ]
  },
  "websocket": {
    "endpoint": "/ws",
    "topics": [
      {
        "name": "session-updates",
        "description": "Real-time session events"
      },
      {
        "name": "context-updates",
        "description": "Real-time context events"
      }
    ]
  }
}
